}
else if (_fastHull.Result.HasCrossedBelow(_slowHull.Result, 0))
{
ClosePositions(TradeType.Buy);

ExecuteMarketOrder(TradeType.Sell, SymbolName, _volumeInUnits, Label, StopLossInPips, TakeProfitInPips);
}
