Print("SenkouSpanA Value = {0}", ichimokuKinkoHyo.SenkouSpanA.LastValue);
Print("SenkouSpanB Value = {0}", ichimokuKinkoHyo.SenkouSpanB.LastValue);
Print("TenkanSen Value = {0}", ichimokuKinkoHyo.TenkanSen.LastValue);

//...
}

