else if (_moneyFlowIndex.Result.Last(1) < 20)
{
ClosePositions(TradeType.Sell);

if (BotPositions.Length == 0)
{
ExecuteMarketOrder(TradeType.Buy, SymbolName, _volumeInUnits, Label, StopLossInPips, TakeProfitInPips);
